import React, { useState, useEffect } from 'react';
import DocEdits from './DocEdits';

const AgendaEdit = ({ sectionId, onClose }) => {
  const [section, setSection] = useState({
    title: '',
    duration: '',
    description: '',
    documents: [],
    presenters: []
  });

  // Fetch section details
  useEffect(() => {
    fetch(`http://localhost:3001/sections/${sectionId}`)
      .then(response => response.json())
      .then(data => setSection(data))
      .catch(error => console.error('Failed to load section:', error));
  }, [sectionId]);

  // Update section details
  const handleSave = () => {
    fetch(`http://localhost:3001/sections/${sectionId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(section)
    })
    .then(response => response.json())
    .then(data => {
      console.log('Updated successfully:', data);
      onClose(); // Optionally close the editor on save
    })
    .catch(error => console.error('Failed to update section:', error));
  };

  const handleChange = (value, field) => {
    setSection(prev => ({ ...prev, [field]: value }));
  };

  // Adjust handlers for add, delete operations similarly
  // Example for delete presenter
  const handleDeletePresenter = (index) => {
    const updatedPresenters = section.presenters.filter((_, i) => i !== index);
    handleChange(updatedPresenters, 'presenters');
  };

  return (
    <div className="fixed top-0 right-0 w-1/3 h-full bg-white shadow-lg p-6 overflow-y-auto z-50">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-bold">Section Details</h2>
        <button className="text-red-600" onClick={onClose}>
          Cancel
        </button>
        <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={handleSave}>
          Save Changes
        </button>
      </div>
      <div className="mb-4">
        <label className="block text-left mb-2">Section Name</label>
        <input
          type="text"
          className="border rounded w-full py-2 px-4"
          value={sectionName}
          onChange={(e) => setSectionName(e.target.value)}
          required
        />
        {!sectionName && <span className="text-red-600">This field is required.</span>}
      </div>
      <div className="mb-4">
        <label className="block text-left mb-2">Duration (minutes)</label>
        <input
          type="number"
          className="border rounded w-full py-2 px-4"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
        />
      </div>
      <div className="mb-4">
        <label className="block text-left mb-2">Section Description</label>
        <textarea
          className="border rounded w-full py-2 px-4"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div className="mb-4">
        <h3 className="text-lg font-semibold">Section Documents</h3>
        <input type="file" multiple onChange={handleAddDocument} />
        <div className="mt-2">
          {documents.map((doc, index) => (
            <div key={index} className="mb-2 flex justify-between items-center">
              <span>{doc.name}</span>
              <DocEdits
                document={doc}
                onDelete={() => handleDeleteDocument(index)}
                onRename={(newName) => handleRenameDocument(index, newName)}
                onPreview={() => handlePreviewDocument(doc)}
                onDownload={() => handleDownloadDocument(doc)}
              />
            </div>
          ))}
        </div>
      </div>
      <div className="mb-4">
        <h3 className="text-lg font-semibold">Presenters</h3>
        <input
          type="text"
          className="border rounded w-full py-2 px-4 mb-2"
          placeholder="Presenter Name"
          value={presenter}
          onChange={(e) => setPresenter(e.target.value)}
        />
        <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={handleAddPresenter}>
          Add
        </button>
        <div className="mt-2">
          {presenters.map((pres, index) => (
            <div key={index} className="mb-2 flex justify-between">
              <span>{pres}</span>
              <button className="text-red-600" onClick={() => handleDeletePresenter(index)}>Delete</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AgendaEdit;
