import React, { useState } from 'react';
import AgendaEdit from './Agendaedit';

const MeetingEdit = () => {
  const [activeTab, setActiveTab] = useState('details');
  const [isAgendaEditOpen, setIsAgendaEditOpen] = useState(false);
  const [sections, setSections] = useState([]);
  const [currentSection, setCurrentSection] = useState(null);

  const handleEditSection = (section) => {
    setCurrentSection(section);
    setIsAgendaEditOpen(true);
  };

  const handleSaveSection = (updatedSection) => {
    if (updatedSection.id) {
      // Update existing section
      setSections((prevSections) =>
        prevSections.map((section) => (section.id === updatedSection.id ? updatedSection : section))
      );
    } else {
      // Create new section
      updatedSection.id = Date.now(); // Mock ID for testing
      setSections([...sections, updatedSection]);
    }
    setIsAgendaEditOpen(false);
    setCurrentSection(null);
  };

  const handleDeleteSection = (sectionId) => {
    setSections((prevSections) => prevSections.filter((section) => section.id !== sectionId));
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'details':
        return <MeetingDetails />;
      case 'agenda':
        return (
          <MeetingAgenda
            sections={sections}
            onEdit={handleEditSection}
            onDelete={handleDeleteSection}
            setSections={setSections}
          />
        );
      case 'analytics':
        return <MeetingAnalytics />;
      default:
        return null;
    }
  };

  return (
    <div className="relative p-6 flex-grow">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-bold">New Meeting</h2>
        <button className="bg-purple-600 text-white px-4 py-2 rounded">Save Changes</button>
      </div>
      <div className="mb-4">
        <nav className="flex border-b">
          <button
            onClick={() => setActiveTab('details')}
            className={`py-2 px-4 ${activeTab === 'details' ? 'border-b-2 border-purple-600 text-purple-600' : ''}`}
          >
            Details
          </button>
          <button
            onClick={() => setActiveTab('agenda')}
            className={`py-2 px-4 ${activeTab === 'agenda' ? 'border-b-2 border-purple-600 text-purple-600' : ''}`}
          >
            Agenda
          </button>
          <button
            onClick={() => setActiveTab('analytics')}
            className={`py-2 px-4 ${activeTab === 'analytics' ? 'border-b-2 border-purple-600 text-purple-600' : ''}`}
          >
            Analytics
          </button>
        </nav>
      </div>
      <div>{renderContent()}</div>
      {isAgendaEditOpen && (
        <AgendaEdit section={currentSection} onClose={() => setIsAgendaEditOpen(false)} onSave={handleSaveSection} />
      )}
    </div>
  );
};

const MeetingDetails = () => {
  const [meetingTitle, setMeetingTitle] = useState('New Meeting');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [meetingAddress, setMeetingAddress] = useState('');
  const [invitees, setInvitees] = useState([{ name: 'Timothy', initials: 'TK' }]);

  const handleSave = () => {
    // Handle save logic, e.g., send data to the backend
    console.log({
      meetingTitle,
      startDate,
      endDate,
      meetingAddress,
    });
  };

  return (
    <div className="bg-gray-100 p-10 rounded-lg">
      <div className="mb-4">
        <h3 className="text-2xl font-semibold mb-4">Meeting Details</h3>
        <div className="mb-4">
          <label className="block text-left mb-2">Meeting Title</label>
          <input
            type="text"
            className="border rounded w-full py-2 px-4"
            value={meetingTitle}
            onChange={(e) => setMeetingTitle(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-left mb-2">Start Date</label>
          <input
            type="datetime-local"
            className="border rounded w-full py-2 px-4"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-left mb-2">End Date</label>
          <input
            type="datetime-local"
            className="border rounded w-full py-2 px-4"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-left mb-2">Meeting Address</label>
          <input
            type="text"
            className="border rounded w-full py-2 px-4"
            value={meetingAddress}
            onChange={(e) => setMeetingAddress(e.target.value)}
          />
        </div>
        <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={handleSave}>
          Save Changes
        </button>
      </div>
    </div>
  );
};

const MeetingAgenda = ({ sections, onEdit, onDelete, setSections }) => {
  const addSection = () => {
    const newSection = { id: Date.now(), title: '', subSections: [] };
    setSections([...sections, newSection]);
  };

  const addSubSection = (sectionId) => {
    const newSubSection = { id: Date.now(), title: '' };
    const updatedSections = sections.map((section) => {
      if (section.id === sectionId) {
        return { ...section, subSections: [...section.subSections, newSubSection] };
      }
      return section;
    });
    setSections(updatedSections);
  };

  return (
    <div className="bg-gray-100 p-10 rounded-lg">
      <h3 className="text-2xl font-semibold mb-4">Agenda</h3>
      <button className="bg-purple-600 text-white px-4 py-2 rounded mb-4" onClick={addSection}>
        + Section
      </button>
      <div>
        {sections.map((section) => (
          <div key={section.id} className="mb-4">
            <input
              type="text"
              placeholder="Section Title"
              className="border rounded w-full py-2 px-4 mb-2"
              value={section.title}
              onChange={(e) => {
                const updatedSections = sections.map((s) => (s.id === section.id ? { ...s, title: e.target.value } : s));
                setSections(updatedSections);
              }}
            />
            <button className="bg-purple-600 text-white px-4 py-2 rounded mb-2" onClick={() => addSubSection(section.id)}>
              + Sub-Section
            </button>
            {section.subSections.map((subSection) => (
              <div key={subSection.id} className="ml-4 mb-2">
                <input
                  type="text"
                  placeholder="Sub-Section Title"
                  className="border rounded w-full py-2 px-4"
                  value={subSection.title}
                  onChange={(e) => {
                    const updatedSections = sections.map((s) => {
                      if (s.id === section.id) {
                        const updatedSubSections = s.subSections.map((sub) =>
                          sub.id === subSection.id ? { ...sub, title: e.target.value } : sub
                        );
                        return { ...s, subSections: updatedSubSections };
                      }
                      return s;
                    });
                    setSections(updatedSections);
                  }}
                />
                <button className="bg-red-600 text-white px-2 py-1 rounded mt-2" onClick={() => onDelete(subSection.id)}>
                  Delete
                </button>
              </div>
            ))}
            <button className="bg-blue-600 text-white px-2 py-1 rounded mt-2" onClick={() => onEdit(section)}>
              Edit
            </button>
            <button className="bg-red-600 text-white px-2 py-1 rounded mt-2" onClick={() => onDelete(section.id)}>
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

const MeetingAnalytics = () => {
  return (
    <div className="bg-gray-100 p-10 rounded-lg">
      <h3 className="text-2xl font-semibold mb-4">Analytics</h3>
      {/* Add analytics content here */}
    </div>
  );
};

export default MeetingEdit;
