import React, { useState } from 'react';
import AgendaEdit from './Agendaedit';

const MeetingEdit = () => {
  const [activeTab, setActiveTab] = useState('details');
  const [isAgendaEditOpen, setIsAgendaEditOpen] = useState(false);
  const [sections, setSections] = useState([]);
  const [currentSection, setCurrentSection] = useState(null);

  const handleEditSection = (section) => {
    setCurrentSection(section);
    setIsAgendaEditOpen(true);
  };

  const handleSaveSection = (updatedSection) => {
    if (updatedSection.id) {
      // Update existing section
      setSections((prevSections) =>
        prevSections.map((section) => (section.id === updatedSection.id ? updatedSection : section))
      );
    } else {
      // Create new section
      updatedSection.id = Date.now(); // Mock ID for testing
      setSections([...sections, updatedSection]);
    }
    setIsAgendaEditOpen(false);
    setCurrentSection(null);
  };

  const handleDeleteSection = (sectionId) => {
    setSections((prevSections) => prevSections.filter((section) => section.id !== sectionId));
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'details':
        return <MeetingDetails />;
      case 'agenda':
        return (
          <MeetingAgenda
            sections={sections}
            onEdit={handleEditSection}
            onDelete={handleDeleteSection}
            setSections={setSections}
          />
        );
      case 'analytics':
        return <MeetingAnalytics />;
      default:
        return null;
    }
  };

  return (
    <div className="relative p-6 flex-grow">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-bold">New Meeting</h2>
        <button className="bg-purple-600 text-white px-4 py-2 rounded">Save Changes</button>
      </div>
      <div className="mb-4">
        <nav className="flex border-b">
          <button
            onClick={() => setActiveTab('details')}
            className={`py-2 px-4 ${activeTab === 'details' ? 'border-b-2 border-purple-600 text-purple-600' : ''}`}
          >
            Details
          </button>
          <button
            onClick={() => setActiveTab('agenda')}
            className={`py-2 px-4 ${activeTab === 'agenda' ? 'border-b-2 border-purple-600 text-purple-600' : ''}`}
          >
            Agenda
          </button>
          <button
            onClick={() => setActiveTab('analytics')}
            className={`py-2 px-4 ${activeTab === 'analytics' ? 'border-b-2 border-purple-600 text-purple-600' : ''}`}
          >
            Analytics
          </button>
        </nav>
      </div>
      <div>{renderContent()}</div>
      {isAgendaEditOpen && (
        <AgendaEdit section={currentSection} onClose={() => setIsAgendaEditOpen(false)} onSave={handleSaveSection} />
      )}
    </div>
  );
};

