import React, { useState} from 'react';
// import { UserContext } from '../Context/UserContext';  // Assume you have a UserContext that provides user data
import { CiSettings } from 'react-icons/ci';
import { IoMdMenu } from 'react-icons/io';
import { IoCalendarNumberSharp } from "react-icons/io5";
import { RiDashboardFill, RiEdit2Fill } from 'react-icons/ri';
import { Link } from 'react-router-dom';


const Sidebar = () => {
  const [collapsed, setCollapsed] = useState(false);


  // const { user } = useContext(UserContext); // Access the user data from UserContext

  const toggleSidebar = () => {
    setCollapsed(!collapsed);
  };


// const getInitials = (name) => {
//     return name.split(' ').map((n) => n[0]).join('');
//   };

  return (

    <div className={`bg-blue-950 text-white ${collapsed ? 'w-16' : 'w-64'} min-h-screen flex flex-col transition-all duration-300 overflow-y-auto`}>
      <div className="p-6 flex items-center justify-between">
        <div className={`text-1xl font-bold flex items-left ${collapsed ? 'hidden' : ''}`}>
          {<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" class="size-12">
              <path d="M4.5 4.5a3 3 0 0 0-3 3v9a3 3 0 0 0 3 3h8.25a3 3 0 0 0 3-3v-9a3 3 0 0 0-3-3H4.5ZM19.94 18.75l-2.69-2.69V7.94l2.69-2.69c.944-.945 2.56-.276 2.56 1.06v11.38c0 1.336-1.616 2.005-2.56 1.06Z" />
            </svg>}
        </div>
        <button onClick={toggleSidebar} className="text-white">
          {collapsed ? <IoMdMenu size={28}/> : <IoMdMenu size={28}/>}
        </button>
      </div>
      {!collapsed && <div className="text-1xl px-6 py-2 text-sm">OnBoardMaster</div>}

    <nav className="mt-4 flex-grow">

      < Link to="/dashboard" className={`px-6 py-3 hover:bg-purple-900 flex items-center ${collapsed ? 'justify-center' : ''}`} title={collapsed ? 'Dashboard' : ''}>
          <span className={collapsed ? '' : 'mr-3'}>
          <RiDashboardFill size={28}/>
          </span>
          {!collapsed && 'Dashboard'}
        </Link>

      <Link to="/meetings" className={`px-6 py-3 hover:bg-purple-900 flex items-center ${collapsed ? 'justify-center' : ''}`} title={collapsed ? 'Meetings' : ''}>
          <span className={collapsed ? '' : 'mr-3'}>
          <IoCalendarNumberSharp size={28}/>
          </span>
          {!collapsed && 'Meetings'}
        </Link>


      <Link to="/actions" className={`px-6 py-3 hover:bg-purple-900 flex items-center ${collapsed ? 'justify-center' : ''}`} title={collapsed ? 'Actions' : ''}>
          <span className={collapsed ? '' : 'mr-3'}>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="size-8">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6.75 3v2.25M17.25 3v2.25M3 18.75V7.5a2.25 2.25 0 0 1 2.25-2.25h13.5A2.25 2.25 0 0 1 21 7.5v11.25m-18 0A2.25 2.25 0 0 0 5.25 21h13.5A2.25 2.25 0 0 0 21 18.75m-18 0v-7.5A2.25 2.25 0 0 1 5.25 9h13.5A2.25 2.25 0 0 1 21 11.25v7.5m-9-6h.008v.008H12v-.008ZM12 15h.008v.008H12V15Zm0 2.25h.008v.008H12v-.008ZM9.75 15h.008v.008H9.75V15Zm0 2.25h.008v.008H9.75v-.008ZM7.5 15h.008v.008H7.5V15Zm0 2.25h.008v.008H7.5v-.008Zm6.75-4.5h.008v.008h-.008v-.008Zm0 2.25h.008v.008h-.008V15Zm0 2.25h.008v.008h-.008v-.008Zm2.25-4.5h.008v.008H16.5v-.008Zm0 2.25h.008v.008H16.5V15Z" />
            </svg>
          </span>
          {!collapsed && 'Actions'}
        </Link>


      <Link to="/notifications" className={`px-6 py-3 hover:bg-purple-900 flex items-center ${collapsed ? 'justify-center' : ''}`} title={collapsed ? 'Notifications' : ''}>
          <span className={collapsed ? '' : 'mr-3'}>
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-8">
         <path stroke-linecap="round" stroke-linejoin="round" d="M14.857 17.082a23.848 23.848 0 0 0 5.454-1.31A8.967 8.967 0 0 1 18 9.75V9A6 6 0 0 0 6 9v.75a8.967 8.967 0 0 1-2.312 6.022c1.733.64 3.56 1.085 5.455 1.31m5.714 0a24.255 24.255 0 0 1-5.714 0m5.714 0a3 3 0 1 1-5.714 0" />
       </svg> 
          </span>
          {!collapsed && 'Notifications'}
        </Link>


      <Link to="/resources" className={`px-6 py-3 hover:bg-purple-900 flex items-center ${collapsed ? 'justify-center' : ''}`} title={collapsed ? 'Resources' : ''}>
          <span className={collapsed ? '' : 'mr-3'}>
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-8">
            <path stroke-linecap="round" stroke-linejoin="round" d="M3.75 9.776c.112-.017.227-.026.344-.026h15.812c.117 0 .232.009.344.026m-16.5 0a2.25 2.25 0 0 0-1.883 2.542l.857 6a2.25 2.25 0 0 0 2.227 1.932H19.05a2.25 2.25 0 0 0 2.227-1.932l.857-6a2.25 2.25 0 0 0-1.883-2.542m-16.5 0V6A2.25 2.25 0 0 1 6 3.75h3.879a1.5 1.5 0 0 1 1.06.44l2.122 2.12a1.5 1.5 0 0 0 1.06.44H18A2.25 2.25 0 0 1 20.25 9v.776" />
          </svg>
          </span>
          {!collapsed && 'Resources'}
      </Link>

      <Link to="/directory" className={`px-6 py-3 hover:bg-purple-900 flex items-center ${collapsed ? 'justify-center' : ''}`} title={collapsed ? 'Directory' : ''}>
          <span className={collapsed ? '' : 'mr-3'}>
