import React, { useState, useEffect } from 'react';
import DocEdits from './Docedits';

const AgendaEdit = ({ section, onClose, onSave }) => {
  const [sectionName, setSectionName] = useState(section.title);
  const [duration, setDuration] = useState(section.duration || '');
  const [description, setDescription] = useState(section.description || '');
  const [documents, setDocuments] = useState(section.documents || []);
  const [presenter, setPresenter] = useState('');
  const [presenters, setPresenters] = useState(section.presenters || []);

  useEffect(() => {
    if (section) {
      setSectionName(section.title);
      setDuration(section.duration || '');
      setDescription(section.description || '');
      setDocuments(section.documents || []);
      setPresenters(section.presenters || []);
    }
  }, [section]);

  const handleAddPresenter = () => {
    if (presenter) {
      setPresenters([...presenters, presenter]);
      setPresenter('');
    }
  };

  const handleAddDocument = (e) => {
    const newDocuments = [...documents, ...Array.from(e.target.files)];
    setDocuments(newDocuments);
  };

  const handleSave = () => {
    const updatedSection = {
      ...section,
      title: sectionName,
      duration,
      description,
      documents,
      presenters,
    };
    onSave(updatedSection);
  };

  const handleDeleteDocument = (index) => {
    const updatedDocuments = documents.filter((_, i) => i !== index);
    setDocuments(updatedDocuments);
  };

  const handleRenameDocument = (index, newName) => {
    const updatedDocuments = documents.map((doc, i) =>
      i === index ? { ...doc, name: newName } : doc
    );
    setDocuments(updatedDocuments);
  };

  const handlePreviewDocument = (document) => {
    // Logic to preview document
  };

  const handleDownloadDocument = (document) => {
    // Logic to download document
  };

  const handleDeletePresenter = (index) => {
    const updatedPresenters = presenters.filter((_, i) => i !== index);
    setPresenters(updatedPresenters);
  };

  return (
    <div className="fixed top-0 right-0 w-1/3 h-full bg-white shadow-lg p-6 overflow-y-auto z-50">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-bold">Section Details</h2>
        <button className="text-red-600" onClick={onClose}>
          Cancel
        </button>
        <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={handleSave}>
          Save Changes
        </button>
      </div>
      <div className="mb-4">
        <label className="block text-left mb-2">Section Name</label>
        <input
          type="text"
          className="border rounded w-full py-2 px-4"
          value={sectionName}
          onChange={(e) => setSectionName(e.target.value)}
          required
        />
        {!sectionName && <span className="text-red-600">This field is required.</span>}
      </div>
      <div className="mb-4">
        <label className="block text-left mb-2">Duration (minutes)</label>
        <input
          type="number"
          className="border rounded w-full py-2 px-4"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
        />
      </div>
      <div className="mb-4">
        <label className="block text-left mb-2">Section Description</label>
        <textarea
          className="border rounded w-full py-2 px-4"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div className="mb-4">
        <h3 className="text-lg font-semibold">Section Documents</h3>
        <input type="file" multiple onChange={handleAddDocument} />
        <div className="mt-2">
          {documents.map((doc, index) => (
            <div key={index} className="mb-2 flex justify-between items-center">
              <span>{doc.name}</span>
              <DocEdits
                document={doc}
                onDelete={() => handleDeleteDocument(index)}
                onRename={(newName) => handleRenameDocument(index, newName)}
                onPreview={() => handlePreviewDocument(doc)}
                onDownload={() => handleDownloadDocument(doc)}
              />
            </div>
          ))}
        </div>
      </div>
      <div className="mb-4">
        <h3 className="text-lg font-semibold">Presenters</h3>
        <input
          type="text"
          className="border rounded w-full py-2 px-4 mb-2"
          placeholder="Presenter Name"
          value={presenter}
          onChange={(e) => setPresenter(e.target.value)}
        />
        <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={handleAddPresenter}>
          Add
        </button>
        <div className="mt-2">
          {presenters.map((pres, index) => (
            <div key={index} className="mb-2 flex justify-between">
              <span>{pres}</span>
              <button className="text-red-600" onClick={() => handleDeletePresenter(index)}>Delete</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AgendaEdit;
